var util = require('util');
var _ = require('lodash');

var app = {};

/**
 * Worker
 *
 * Attaches defined data to parameter which inherit the data.
 * It uses 2 functions, preProcess and postProcess (with the result of preProcess).
 *
 * preProcess  Generates a list with [defineName][name][version] = value
 * postProcess Attach the preProcess data with the nearest version to the tree.
 *
 * @param {Object} _app
 */
function Worker(_app) {
    var self = this;

    // global variables
    app = _app;

    // class variables
    this.workers = {};

    // load worker
    var workers = Object.keys(app.workers);
    workers.forEach(function(worker) {
        if (_.isObject( app.workers[worker] )) {
            app.log.debug('inject worker: ' + worker);
            self.addWorker(worker, app.workers[worker] );
        } else {
            var filename = app.workers[worker];
            app.log.debug('load worker: ' + worker + ', ' + filename);
            self.addWorker(worker, require(filename));
        }
    });
}

/**
 * Inherit
 */
util.inherits(Worker, Object);

/**
 * Exports
 */
module.exports = Worker;

/**
 * Add Worker
 */
Worker.prototype.addWorker = function(name, worker) {
    this.workers[name] = worker;
};

/**
 * Execute worker
 *
 * @todo Add priority system (if needed), if a plugin need an other operation to be done before.
 */
Worker.prototype.process = function(parsedFiles, parsedFilenames, packageInfos) {
    // some smaller operation that are not outsourced to extra workers
    // TODO: add priority system first and outsource them then
    parsedFiles.forEach(function(parsedFile, fileIndex) {
        parsedFile.forEach(function(block) {
            if (Object.keys(block.global).length === 0 && Object.keys(block.local).length > 0) {
                if ( ! block.local.type)
                    block.local.type = '';

                if ( ! block.local.url)
                    block.local.url = '';

                if ( ! block.local.version)
                    block.local.version = packageInfos.defaultVersion;

                if ( ! block.local.filename)
                    block.local.filename = parsedFilenames[fileIndex];

                // convert dir delimeter \\ to /
                block.local.filename = block.local.filename.replace(/\\/g, '/');
            }

        });
    });

    // process transformations and assignments for each @api-Parameter
    var preProcessResults = {};

    _.each(this.workers, function(worker, name) {
        if (worker.preProcess) {
            app.log.verbose('worker preProcess: ' + name);
            var result = worker.preProcess(parsedFiles, parsedFilenames, packageInfos);
            _.extend(preProcessResults, result);
        }
    });
    _.each(this.workers, function(worker, name) {
        if (worker.postProcess) {
            app.log.verbose('worker postProcess: ' + name);
            worker.postProcess(parsedFiles, parsedFilenames, preProcessResults, packageInfos);
        }
    });
};

var _    = require('lodash');
var path = require('path');
var util = require('util');
var glob = require('glob');

var app = {};

function PluginLoader(_app) {
  var self = this;

  // global variables
  app = _app;

  // class variables
  self.plugins = {};

  // Try to load global apidoc-plugins (if apidoc is installed locally it tries only local)
  this.detectPugins(__dirname);

  // Try to load local apidoc-plugins
  this.detectPugins( path.join(process.cwd(), '/node_modules') );

  if (Object.keys(this.plugins).length === 0) {
    app.log.debug('No plugins found.');
  }

  this.loadPlugins();
}
/**
 * Inherit
 */
util.inherits(PluginLoader, Object);

/**
 * Exports
 */
module.exports = PluginLoader;

/**
 * Detect modules start with "apidoc-plugin-".
 * Search up to root until found a plugin.
 */
PluginLoader.prototype.detectPugins = function(dir) {
  var self = this;

  // Every dir start with "apidoc-plugin-", because for the tests of apidoc-plugin-test.
  var plugins;
  try {
    plugins = glob.sync(dir + '/apidoc-plugin-*')
    .concat( glob.sync(dir + '/@*/apidoc-plugin-*') );
  } catch (e) {
    app.log.warn(e);
    return;
  }

  if (plugins.length === 0) {
    dir = path.join(dir, '..');
    if (dir === '/' || dir.substr(1) === ':\\') {
      return;
    }
    return this.detectPugins(dir);
  }

  var offset = dir.length + 1;
  plugins.forEach( function(plugin) {
      var name = plugin.substr(offset);
      var filename = path.relative(__dirname, plugin);
      app.log.debug('add plugin: ' + name + ', ' + filename);
      self.addPlugin(name, plugin);
  });
};

/**
 * Add Plugin to plugin list.
 */
PluginLoader.prototype.addPlugin = function(name, filename) {
  if (this.plugins[name]) {
    app.log.debug('overwrite plugin: ' + name + ', ' + this.plugins[name]);
  }

  this.plugins[name] = filename;
};

/**
 * Load and initialize Plugins.
 */
PluginLoader.prototype.loadPlugins = function() {
  _.forEach(this.plugins, function(filename, name) {
    app.log.debug('load plugin: ' + name + ', ' + filename);
    var plugin;
    try {
      plugin = require(filename);
    } catch(e) {
    }
    if (plugin && plugin.init) {
      plugin.init(app);
    } else {
      app.log.debug('Ignored, no init function found.');
    }
  });
};

